export const newConfig = [
  {
    head: "ES_NEW_APPLICATION_PROPERTY_ASSESSMENT",
    body: [
      {
        route: "docs-required",
        component: "WSDocsRequired",
        key: "data",
        nextStep: "connection-holder"
