              t={t}
              optionKey="code"
              name="SearchType"
              options={SearchTypes}
              value={searchType}
              selectedOption={searchType}
              onSelect={selectSearchType}
              {...(validation = {
                isRequired: true,
                title: t("WS_SEARCH_TYPE_MANDATORY"),
              })}
          />
        <CardLabel>{`${t("WS_PROP_DETAIL_CITY")}*`}</CardLabel>
        <RadioOrSelect
