                option={Menu}
                select={setTypeOfOwner}
                optionKey="i18nKey"
                t={t}
            />
            {ownerType && Object.entries(ownerType).length>0 && ownerType?.code !== "NONE" && <div>
                <CardLabel>{`${t("WS_DOCUMENT_ID_LABEL")}`}</CardLabel>
                <TextInput
                    t={t}
