            setValue("offset", 0);
            setValue("limit", 10);
            setValue("sortBy","commencementDate");
            setValue("sortOrder","DESC");
            setValue("isSubmitSuccessful","false");
            reset({
              applicationNo: "",
              sourceRefId: "",
              nocType: "",
              nocNo: "",
              mobileNumber: "",
              offset: 0,
              limit: 10,
              sortBy: "commencementDate",
              sortOrder: "DESC",
              "isSubmitSuccessful":false,
            });
            previousPage();
          }}
        >
          {t(`ES_COMMON_CLEAR_ALL`)}
        </p>
      </SearchField>
    </>
  );
};

